import React from 'react'
import { ReactDOM } from 'react';
import { render } from 'react-dom';
import Header from '../../Header/Header.jsx'
import { BrowserRouter } from 'react-router-dom';


let questions =[
    [`Вопрос 1`,["Ответ 1",0],["Ответ 2",1], ["Ответ 3",0]],
    [`Вопрос 2`,["Ответ 1",0],["Ответ 2",0], ["Ответ 3",0]]
];


let listItems = [];
for (let i of questions) {
    let l = 0;
    for (let k of i) {
        if(l==0){
            listItems.push(<div>
                <input type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k}></input>
                </div>)
        }
        else{
            listItems.push(<div className="form-check">
            <label>
            <input
                type="radio"
                name="react-tips"
                value={0}
                checked={false}
                className="form-check-input"
            />
            <input type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k[0]}></input>
            </label>
                
            <input type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k[1]}></input> 
            </div>)
        }
        l++;
    }
}

for (let i of questions) {
    listAnswers = [];
    num = 0;
    for (let k of i) {
        if(num!=0){
            listAnswers.push(<div className="form-check">
            <label>
            <input
                type="radio"
                name="react-tips"
                value={k[1]}
                checked={true}
                className="form-check-input"
            /><input type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k[0]}></input>
            </label>
            </div>);
            
        }else{
            num++;
        }
    }

    listItems.push(<div>
        <div className="AnswerSelect">
            <p1><input type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k}></input></p1>
            <form>
                <div className="form-check">
                    {listAnswers}
                </div>
            </form>                              
        </div>
    </div>);
}

const addQuestion = () => {
    questions.push([`Вопрос ${questions.length+1}`,["Ответ 1",0],["Ответ 2", 0],["Ответ 3", 0]]);
    listItems = [];
    for (let i of questions) {
        let l = 0;
        for (let k of i) {
            if(l==0){
                listItems.push(<div><input type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k}></input></div>)
            }
            else{
                listItems.push(<div><input type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k[0]}></input>
                <input type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k[1]}></input> </div>)
            }       
            l++; 
        }
    }
    render(<div><button onClick={() => addQuestion()}>Add</button>{listItems}</div>, document.getElementById("ren"));
};



export const CreateTest = () => {
    return (
      <div className='Content'>
        <div className="Create">
            <div className="CreateHeader">
                <h1>Составь вопросы</h1>
                <div className="MainCreate">
                    <img src={'./img/createtest.jpg'} alt="" className='CreateImg'/>
                    <div className="CreateQuestions">
                        <form>
                            <p>Название</p>
                            <input type="text" className="QuestHeader" />
                            <p>Описание</p>
                            <input type="text" className="QuestMessage" />
                        </form>
                    </div>
                    <div id="ren">
                        <button onClick={() => addQuestion()}>Add</button>
                        {listItems}
                    </div>
                </div>
            </div>
        </div>
      </div>
    )
    
  }
