import React from 'react'
import { ReactDOM } from 'react';
import { render } from 'react-dom';



let questions =[
    ["Question 1","1","2"],
    ["Question 2","1","2"]
];
let listItems = questions.map((number) => <div>number.map((ex)=> <input type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} value = {ex}/>)</div>);

const addQuestion = () => {
    questions.push([`Вопрос ${questions.length+1}`,"Ответ 1","Ответ 2", "Ответ 3"]);
    listItems = questions.map((number) => number.map((ex)=> <input type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} value = {ex}/>));
    render(<CreateTest />, document.getElementById("root"));
};

const Item = (
    <div>
    </div>
);


export const CreateTest = () => {
    return (
      <div className='Content'>
        <div className="Create">
            <div className="CreateHeader">
                <h1>Составь вопросы</h1>
                <div className="MainCreate">
                    <img src={'./img/createtest.jpg'} alt="" className='CreateImg'/>
                    <div className="CreateQuestions">
                        <form>
                            <p>Название</p>
                            <input type="text" className="QuestHeader" />
                            <p>Описание</p>
                            <input type="text" className="QuestMessage" />
                        </form>
                    </div>
                    <div>
                        <button onClick={() => addQuestion()}>Add</button>
                        <div>{listItems}</div>
                    </div>
                </div>
            </div>
        </div>
      </div>
    )
    
  }
