import React from 'react'
import {Link} from "react-router-dom"

export const SignUpPage = () => {
    const [register, setRegister] = useState(() => {
        return {
            name: "",
            surname: "",
            email: "",
            password: "",
            password2: "",
        }
    })
     
    const changeInputRegister = event => {
        event.persist()
        setRegister(prev => {
            return {
                ...prev,
                [event.target.name]: event.target.value,
            }
        })
    }
     
     
    const submitChackin = event => {
        event.preventDefault();
        if(!validator.isEmail(register.email)) {
            alert("You did not enter email")
        } else if(register.password !== register.password2) {
            alert("Repeated password incorrectly")
        } else if(!validator.isStrongPassword(register.password, {minSymbols: 0})) {
            alert("Password must consist of one lowercase, uppercase letter and number, at least 8 characters")
        } else {
            axios.post(DOMEN_SERVER + "/auth/registration/", {
                name: register.name,
                surname: register.surname,
                email: register.email,
                password: register.password,
            }).then(res => {
                if (res.data === true) {
                    window.location.href = DOMEN_SITE + "/auth"
                } else {
                    alert("There is already a user with this email")
                }
            }).catch(() => {
                alert("An error occurred on the server")
            })
        }
    }
    return (
      <div className='Content'>
        <div className='SignUp'>
            <h1 className='HeaderSignUp'>Регистрация</h1>
            <div className="SignUpMain">
            <div className='TextSignUp'>
                <div className="SignUpForm">
                    <form>
                        <p>Имя</p>
                        <input type="text" name="name" placeholder=''/>
                        <p>Фамилия</p>
                        <input type="text" name="surname" placeholder=''/>
                        <p>Email</p>
                        <input type="email" name="email" placeholder=''/>
                        <p>Пароль</p>
                        <input type="password" name="password" placeholder=''/>
                        <p>Подтверждение пароля</p>
                        <input type="text" name="password2" placeholder=''/>
                    </form>
                    <button>SIGN IN</button>
                </div>
                <div className="SignUpLinks">
                <Link to = '/signinto' style = {{"textDecoration" : "none", "color": "#000 !important"}}>
                    <p>Already registrated?</p>
                </Link>
                </div>
            </div>
            <div className='ImgSignUp'>
                <img src={"./img/people2.jpg"} />
            </div>
            </div>
        </div>
      </div>
    )
  }
