import React from 'react'

class ItemQuestion extends React.Component {
    state = {
      count: 0,
    }

    onClick = () => {
      this.setState(({ count }) => ({
        count: count + 1,
      }));
    }

    render() {
      return (
        <div>
          <button onClick={this.onClick}>Добавить компонент</button>
          {[...Array(this.state.count)].map(() => <Input />)}
        </div>
      );
    }
  }
