import React from 'react'
import { ReactDOM } from 'react';
import { render } from 'react-dom';


const TestData = {
        "id": 1,
        "testName": "Are you seek?",
        "description": "Yes",
        "questions": [
        {
            "id": 1,
            "question": "Why are you worked there",
            "subanswers": [
                {
                    "id": 1,
                    "answer": "This is me",
                    "score": 99
                },
                {
                    "id": 2,
                    "answer": "This is",
                    "score": 23
                }
            ]
        },
        {
            "id": 4,
            "question": "Is not a quest",
            "subanswers": []
        },
        {
            "id": 3,
            "question": "Is a quest",
            "subanswers": []
        }
    ]
}


let TestName;
let TestDesc;

const questions = TestData.questions.map(item => {
    const res = [item.question];
    item.subanswers.forEach(subItem => res.push([subItem.answer, subItem.score]));
    return (res);
});


let listItems = [];
let listAnswers = [];
let num = 0;
for (let i of questions) {
    listAnswers = [];
    num = 0;
    for (let k of i) {
        if(num!=0){
            listAnswers.push(<div className="form-check">
            <label>
            <input
                type="radio"
                name="react-tips"
                value="quest1"
                checked={true}
                className="form-check-input"
            />{k}
            </label>
            </div>);
            
        }else{
            num++;
        }
    }

    listItems.push(<div>
        <div className="AnswerSelect">
            <p1>{i[0]}</p1>
            <form>
                <div className="form-check">
                    {listAnswers}
                </div>
            </form>                              
        </div>
    </div>);
}




export const TestReady = () => {
    return (
      <div className='Content'>
        <div className="Create">
            <div className="CreateHeader">
                <h1>Составь вопросы</h1>
                <div className="MainCreate">
                    <img src={'./img/createtest.jpg'} alt="" className='CreateImg'/>
                    <div className="CreateQuestions">
                        <form>
                            <p>Название</p>
                            <div className="QuestMessage">{TestName}</div>
                            <p>Описание</p>
                            <div className="QuestMessage">{TestDesc}</div>
                        </form>
                    </div>
                    {listItems}
                </div>
            </div>
        </div>
      </div>
    )
    
  }
