import React, { useState } from 'react'
import { create, all } from 'mathjs'

const config = { }
const math = create(all, config)


const numKeys = ["%", "CE", "C", "←", "1/x", "x²", "√x", "÷", "7", "8", "9", "×", "4", "5", "6", "-", "1", "2", "3", "+", "+/-", "0", ".", "="];




export const MainPage = () => {
  let phrase = "10.5+5^2";
  let currentNum = "10";

  let listBut = [];
  for(let i =0;i<numKeys.length;i++){
    let styleN = ''
    if(numKeys[i] == "="){
     listBut.push( <button className="buttonKey" value={numKeys[i]}><p className='textButton' onClick={() => evaluatePhrase(phrase)}>{numKeys[i]}</p></button>);
    }else{
      if(i<7 || i%4==3){
        styleN='buttonElse';
      }
      listBut.push( <button className={styleN} value={numKeys[i]}><p className='textButton'>{numKeys[i]}</p></button>);
    }
  }
  const evaluatePhrase = (phrase) => {
    alert(math.evaluate(phrase));
  }
  const inputChange = (event) => {
    phrase = phrase + event;
    console.log(phrase);
  }
  return (
    <div className='Content'>
        <div className='logo'></div>
        <p className='Title'>Standart</p>
        <p className='viewPhr'>{phrase}</p>
        <input className='Screen' type='text' defaultValue={currentNum} onChange={event => inputChange(event.target.value)}></input>
        <div className='Item'>{listBut}</div>
    </div>
  )
}
