import React from 'react'
import { ReactDOM } from 'react';
import { render } from 'react-dom';
import Header from '../../Header/Header.jsx'
import { BrowserRouter } from 'react-router-dom';



let questions = [];

let listItems = [];
let listAnswers = [];


const addQuestion = () => {
    questions.push([`Вопрос ${questions.length+1}`,["Ответ 1",0],["Ответ 2", 0],["Ответ 3", 0]]);
    listItems = [];
    for (let i of questions) {
        listAnswers = [];
        let num = 0;
        for (let k of i) {
            if(num!=0){
                listAnswers.push(<div className="form-check">
                <label>
                <input
                    type="radio"
                    name="react-tips"
                    value={k[1]}
                    checked={false}
                    className="form-check-input"
                /><input name="answer" type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k[0]}></input>
                <input name="answer" type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k[1]}></input>
                </label>
                </div>);
                
            }else{
                num++;
            }
        }
    
        listItems.push(<div>
            <div className="AnswerSelect">
                <p1><input name="question" type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={i[0]}></input></p1>
                <form>
                    <div className="form-check">
                        {listAnswers}
                    </div>
                </form>                              
            </div>
        </div>);
    }
    render(<div><button className="firststart" onClick={() => addQuestion()} >Add Question</button><div>{listItems}</div></div>, document.getElementById("ren"));
};



export const CreateTest = () => {
    const currentUrl = window.location.href;
    console.log(currentUrl);

    questions =[
        [`Вопрос 1`,["Ответ 1",0],["Ответ 2",0], ["Ответ 3",0]]
    ];


    let num = 0;
    for (let i of questions) {
        listAnswers = [];
        num = 0;
        for (let k of i) {
            if(num!=0){
                listAnswers.push(<div className="form-check">
                <label>
                <input
                    type="radio"
                    name="react-tips"
                    value={k[1]}
                    checked={false}
                    className="form-check-input"
                /><input name="answer" type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k[0]}></input>
                <input name="answer" class="input_2" type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={k[1]}></input>
                </label>
                </div>);
            
            }else{
                num++;
            }
        }

        listItems.push(<div>
            <div className="AnswerSelect">
                <p1><input name="question" type="text" onChange={(event)=>{this.setState({coinAvalue:event.target.value})}} defaultValue={i[0]}></input></p1>
                <form>
                    <div className="form-check">
                        {listAnswers}
                    </div>
                </form>                              
            </div>
        </div>);
    }
    return (
      <div className='Content'>
        <div className="Create">
            <div className="CreateHeader">
                <h1>Составь вопросы</h1>
                <div className="MainCreate">
                    <img src={'./img/createtest.jpg'} alt="" className='CreateImg'/>
                    <div className="CreateQuestions">
                        <form>
                            <p>Название</p>
                            <input type="text" className="QuestHeader" />
                            <p>Описание</p>
                            <input type="text" className="QuestMessage" />
                        </form>
                    </div>
                    <div id="ren">
                        <button className="firststart" onClick={() => addQuestion()}>Add Question</button>
                        <div>{listItems}</div>
                    </div>
                </div>
            </div>
        </div>
      </div>
    )
    
  }
